{-# LANGUAGE UnicodeSyntax, OverloadedStrings, QuasiQuotes #-}
--
-- CSS to go with an HTML-rendered diff tree.
--
module ExtRep.CssFontIcon (iconFontFace, mkFontIcon) where

import BaseImport
import Clay
import Text.Heredoc



iconFontFace ∷ Css
iconFontFace = fontFace $ do
             fontFamily ["diff-tree-icomoon"] []
             fontStyle normal 
             fontWeight normal
             fontFaceSrc [ FontFaceSrcUrl embeddedFontData (Just WOFF) ]

fontIcon ∷ Css
fontIcon = do
         fontFamily ["diff-tree-icomoon"] []
         "speak" -: "none"
         fontStyle normal
         fontWeight normal
         fontVariant normal
         textTransform none
         "lineHeight" -: "1"
         
         -- Better Font Rendering =========== */
         "-webkit-font-smoothing" -: "antialiased"
         "-moz-osx-font-smoothing" -: "grayscale"

mkFontIcon ∷ Text → Css
mkFontIcon charCode = do
                    fontIcon
                    content (stringContent charCode)

embeddedFontData = [here|data:application/font-woff;charset=utf-8;base64,|]
